import './Seats2.css'
import React, { useState } from 'react'
import clsx from 'clsx'
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";
import { theatreSelected } from "./Theatres.js";
import { movieSelected } from "./Movies.js";
import { stSelected } from "./Showtimes.js";

const s= 'test4'
const p=20.00

const movies = [
  {
    name: 'Avenger',
    price: 10,
    occupied: [20, 21, 30, 1, 2, 8],
  },
  {
    name: 'Joker',
    price: 12,
    occupied: [9, 41, 35, 11, 65, 26],
  },
  {
    name: 'Toy story',
    price: 8,
    occupied: [37, 25, 44, 13, 2, 3],
  },
  {
    name: 'the lion king',
    price: 9,
    occupied: [10, 12, 50, 33, 28, 47],
  },
]

const seats = Array.from({ length: 8 * 8 }, (_, i) => i)

export default function Seats2() {
    const nav = useNavigate();
  const [selectedMovie, setSelectedMovie] = useState(movies[0])
  const [selectedSeats, setSelectedSeats] = useState([])
  const [isError, setIsError] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleClick=(e)=>{
    e.preventDefault()
    const ticket={
      theatre: theatreSelected.name, 
      movie: movieSelected.name, 
      showtime: stSelected.showtime, 
      seat: s, 
      price: p}
    console.log(ticket)
    // TODO: send data to database
    fetch("http://localhost:8080/api/v1/ticket/add", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(ticket),
        })
          .then(() => {
            console.log(ticket);
            console.log("Ticket Created");
            setIsSubmitted(true);
            setIsError(false);
            nav("/movie-ticket-system/payment");
          })
          .catch(() => {
            console.log("Error");
            setIsError(true);
            setIsSubmitted(false);
          });
  }

  return (
    <div className="App">
      <Movies
        movie={selectedMovie}
        onChange={movie => {
          setSelectedSeats([])
          setSelectedMovie(movie)
        }}
      />
      <ShowCase />
      <Cinema
        movie={selectedMovie}
        selectedSeats={selectedSeats}
        onSelectedSeatsChange={selectedSeats => setSelectedSeats(selectedSeats)}
      />

      <p className="info">
        You have selected <span className="count">{selectedSeats.length}</span>{' '}
        seats for the price of{' '}
        <span className="total">
          {selectedSeats.length * selectedMovie.price}$
        </span>
      </p>
      <Button variant="contained" onClick={handleClick}>
        Submit
      </Button>
    </div>
  )
}

function Movies({ movie, onChange }) {
  // return (
  //   <div className="Movies">
  //     <label htmlFor="movie">Pick a movie</label>
  //     <select
  //       id="movie"
  //       value={movie.name}
  //       onChange={e => {
  //         onChange(movies.find(movie => movie.name === e.target.value))
  //       }}
  //     >
  //       {movies.map(movie => (
  //         <option key={movie.name} value={movie.name}>
  //           {movie.name} (${movie.price})
  //         </option>
  //       ))}
  //     </select>
  //   </div>
  // )
}

function ShowCase() {
  return (
    <ul className="ShowCase">
      <li>
        <span className="seat" /> <small>N/A</small>
      </li>
      <li>
        <span className="seat selected" /> <small>Selected</small>
      </li>
      <li>
        <span className="seat occupied" /> <small>Occupied</small>
      </li>
    </ul>
  )
}

function Cinema({ movie, selectedSeats, onSelectedSeatsChange }) {
  function handleSelectedState(seat) {
    const isSelected = selectedSeats.includes(seat)
    if (isSelected) {
      onSelectedSeatsChange(
        selectedSeats.filter(selectedSeat => selectedSeat !== seat),
      )
    } else {
      onSelectedSeatsChange([...selectedSeats, seat])
    }
  }

  return (
    <div className="Cinema">
      <div className="screen" />

      <div className="seats">
        {seats.map(seat => {
          const isSelected = selectedSeats.includes(seat)
          const isOccupied = movie.occupied.includes(seat)
          return (
            <span
              tabIndex="0"
              key={seat}
              className={clsx(
                'seat',
                isSelected && 'selected',
                isOccupied && 'occupied',
              )}
              onClick={isOccupied ? null : () => handleSelectedState(seat)}
              onKeyPress={
                isOccupied
                  ? null
                  : e => {
                      if (e.key === 'Enter') {
                        handleSelectedState(seat)
                      }
                    }
              }
            />
          )
        })}
      </div>
    </div>
  )
}
