import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container'
import Paper from '@mui/material/Paper'
import Button from '@mui/material/Button'
import { useState, useEffect } from 'react';

export default function TheatreList() {
  const paperStyle = {padding:'50px 20px', width:600, margin:'20px auto'}
  const [theatres,setTheatres] = useState([])

  useEffect(()=>{
    fetch("http://localhost:8080/api/v1/theatres")
    .then(res=>res.json())
    .then(result=>{
      setTheatres(result);
    })
  },[])

  return (
    <Container>
      <Paper elevation={3} style={paperStyle}>
      <h1>Theatres</h1>
      {courses.map(course=>(
          <Paper elevation={6} style={{margin:"10px",padding:"15px",textAlign:"left"}} key={course.id}>
            ID:{course.id}, 
            Name:{course.name},
            Capacity:{course.capacity} <br></br>
            Start Time:{course.startTime},
            End Time:{course.endTime} <br></br>
            Has Prerequisite:{course.hasPrerequisite ? 'yes' : 'no'}, <br></br>
            Enrolled Students:{course.enrolledStudents}
          </Paper>
        ))}
      </Paper>

    </Container>
  );
}
