				console.log(student);
				console.log("New Student Added");
				setIsSubmitted(true);
				setIsError(false);
			})
			.catch(() => {
				console.log("Error");
				setIsError(true);
				setIsSubmitted(false);
			});

    fetch(`http://localhost:8080/api/v1/payment/addPayment/${userInfo.id}/${price}`,
        {
          method:"PUT",
          headers:{"Content-Type":"application/json"},
          body:JSON.stringify(creditCard)
        }).then(()=>{
      console.log("Payment Successful")
      setIsSubmitted(true);
      setIsError(false);

      console.log("getting user info")
      fetch(`http://localhost:8080/api/v1/registereduser/getUser/${userInfo.id}`)
      .then(res=>res.json())
      .then(result=>{
        console.log(result)
        userInfo.accountBalance = result;
        nav("/movie-ticket-system/theatres");
      })
    })
    .catch(() => {
      console.log("err2");
      setIsError(true);
      setIsSubmitted(false);
    }).catch(()=>{
      console.log("Error")
      setIsError(true);
      setIsSubmitted(false);
    })
}

  return (
    <Container>
      <Paper elevation={3} style={paperStyle}>
        <h1>Annual Fee Payment</h1>
        <p>Your total is ${totalPrice}</p>

    <Box
      component="form"
      sx={{
        '& > :not(style)': { m: 1, width: 500, maxWidth: '100%' },
      }}
      noValidate
      autoComplete="off"
    >

      <Button variant="contained" onClick={handleClick}>
        Pay
      </Button>
    </Box>
    </Paper>

      {/* <Paper elevation={3} style={paperStyle}>
