import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container'
import Paper from '@mui/material/Paper'
import Button from '@mui/material/Button'
import { useState, useEffect } from 'react';
import { LinkContainer } from "react-router-bootstrap";

export default function TheatreList() {
  const paperStyle = {padding:'50px 20px', width:600, margin:'20px auto'}
//   const [theatres,setTheatres] = useState([])
  const [theatres,setTheatres] = useState(['test1', 'test2'])

  useEffect(()=>{
    fetch("http://localhost:8080/api/v1/theatres")
    .then(res=>res.json())
    .then(result=>{
      setTheatres(result);
    })
  },[])

  return (
    <Container>
      <Paper elevation={3} style={paperStyle}>
      <h1>Theatres</h1>
        {theatres.map(theatre=>(
          <Paper elevation={6} style={{margin:"10px",padding:"15px",textAlign:"left"}} key={theatre.id}>
            {/* Name:{theatre.name} */}
            <LinkContainer to="/movie-ticket-system/">
                Name:{theatre}
            </LinkContainer>
          </Paper>
        ))}
      </Paper>

    </Container>
  );
}
